generated.css:
.nav-item {
  text-align: center;
  font-family: "Montserrat", sans-serif;
}

.container {
  padding: 30px 0;
  padding-bottom: 50px;
}

.panel-group .panel {
  background: #fff;
  border-radius: 2px;
  box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.1);
  margin-bottom: 10px;
}
.panel-group .panel .panel-heading {
  background: #fafafa;
  border-top-left-radius: 2px;
  border-top-right-radius: 2px;
}
.panel-group .panel .panel-heading a {
  display: block;
  width: 100%;
  text-align: left;
  padding: 15px 30px;
  font-size: 20px;
  text-align: left;
  color: #205d85;
  text-decoration: none;
}
@media only screen and (max-width: 769px) {
  .panel-group .panel .panel-heading a {
    font-size: 16px;
  }
}
.panel-group .panel .panel-collapse .panel-body {
  text-align: left;
  padding: 15px 30px 30px 30px;
}
@media only screen and (max-width: 769px) {
  .panel-group .panel .panel-collapse .panel-body {
    font-size: 14px;
  }
}
.panel-group .panel .panel-collapse .panel-body a {
  color: #3d95d1;
  font-weight: bold;
}
.panel-group .panel .panel-collapse .panel-body a img.logo {
  margin: 10px;
  width: 120px;
}
.panel-group .panel .panel-collapse .panel-body p:last-child {
  margin-bottom: 0;
}
.panel-group .panel .panel-collapse .panel-body h3 {
  font-size: 18px;
  margin-top: 25px;
  font-weight: 600;
}
.panel-group .panel .panel-collapse .panel-body h3.mt-0 {
  margin-top: 0;
}
.panel-group .panel .panel-collapse .panel-body h4 {
  font-size: 16px;
  font-weight: 600;
  margin-top: 20px;
}
.panel-group .panel.panel-static .panel-heading {
  background: #3d95d1;
}
.panel-group .panel.panel-static .panel-heading a {
  color: #fff;
}

.about-eu-funding {
  text-align: center;
  font-family: Arial;
  margin: 20px auto;
}
.about-eu-funding img {
  width: 200px;
}
.about-eu-funding p {
  font-size: 13px;
  margin-top: 10px;
}

/* 404 PAGE */
.container-404 {
  padding: 30px 0;
  padding-bottom: 50px;
  font-family: "Montserrat", sans-serif;
  text-align: center;
  background: #3b95d0;
  min-height: 100vh;
  color: #fff;
  display: flex;
  align-items: center;
  justify-content: center;
}
.container-404 .content {
  background: white;
  padding: 30px;
  width: 90%;
  max-width: 600px;
  border-radius: 10px;
  color: #3b95d0;
}
.container-404 .title {
  font-size: 80px;
}
.container-404 .subtitle {
  font-size: 20px;
}
.container-404 .cta {
  margin: 30px auto;
  max-width: 400px;
}
.container-404 .cta a {
  color: #fff;
  background: #3b95d0;
  display: block;
  padding: 5px 20px;
  border-radius: 3px;
  font-weight: 600;
  text-decoration: none;
  font-size: 20px;
}
